import React, { useEffect, useState } from "react";
import CodeEditorSide from "../CodeEditor/CodeEditorSide";
import CodeOutputSide from "../CodeOutput/CodeOutputSide";
import CodeEditorTop from "../CodeEditor/CodeEditor";
import CodeOutputTop from "../CodeOutput/CodeOutput";
import { SelectedViewContext } from "../../context/SelectedView";

function ExploreCodingPage(srcDocVal) {
  const { selectedView } = React.useContext(SelectedViewContext);
  const [finalSrcDoc, setFinalSrcDoc] = useState(` <!DOCTYPE html>
      <html lang="en" style="height: 100%; width: 100%;">
        <head>
          <meta charset="UTF-8">
          <meta name="viewport" content="width=device-width, initial-scale=1.0">
          <style>
            html, body {
              height: 100%;
              width: 100%;
              margin: 0;
              padding: 0;
            }
          </style>
          <!-- Include any additional head elements here -->
          <style>${srcDocVal.css}</style>
        </head>
        <body>${srcDocVal.html}</body>
        <script>${srcDocVal.js}</script>
      </html>`);

  useEffect(() => {
    const timeout = setTimeout(() => {
      setFinalSrcDoc(`
       <!DOCTYPE html>
      <html lang="en" style="height: 100%; width: 100%;">
        <head>
          <meta charset="UTF-8">
          <meta name="viewport" content="width=device-width, initial-scale=1.0">
          <style>
            html, body {
              height: 100%;
              width: 100%;
              margin: 0;
              padding: 0;
            }
          </style>
          <!-- Include any additional head elements here -->
          <style>${srcDocVal.css}</style>
        </head>
        <body>${srcDocVal.html}</body>
        <script>${srcDocVal.js}</script>
      </html>
      `);
    }, 250);

    return () => clearTimeout(timeout);
  }, [html, css, js]);

  const TopView = () => {
    return (
      <div className="codingPage">
        <div className="codingPage-main-container1">
          <CodeEditorTop />
        </div>
        <div className="codingPage-main-container2">
          <CodeOutputTop srcDoc={finalSrcDoc} />
        </div>
      </div>
    );
  };

  const LeftView = () => {
    return (
      <div className="codingPage1">
        <div className="codingPage-main-container3">
          <CodeEditorSide />
        </div>
        <div className="codingPage-main-container4">
          <CodeOutputSide srcDoc={finalSrcDoc} />
        </div>
      </div>
    );
  };

  const RightView = () => {
    return (
      <div className="codingPage2">
        <div className="codingPage-main-container3">
          <CodeEditorSide />
        </div>
        <div className="codingPage-main-container4">
          <CodeOutputSide srcDoc={finalSrcDoc} />
        </div>
      </div>
    );
  };

  return (
    <>
      {selectedView === "top" ? TopView() : null}
      {selectedView === "left" ? LeftView() : null}
      {selectedView === "right" ? RightView() : null}
    </>
  );
}

export default ExploreCodingPage;
