import React from "react";
import { ex1, ex2, ex3, ex4, ex5, examplesExplore } from "./ExploreFun";
import "./explore.scss";

function ExplorePage() {
  const exportModule = (exmp) => {
    const { html, css, js } = exmp;
    return `
    <!DOCTYPE html>
      <html lang="en" style="height: 100%; width: 100%;">
        <head>
          <meta charset="UTF-8">
          <meta name="viewport" content="width=device-width, initial-scale=1.0">
          <style>
            html, body {
              height: 100%;
              width: 100%;
              margin: 0;
              padding: 0;
            }
          </style>
          <!-- Include any additional head elements here -->
          <style>${css}</style>
        </head>
        <body>${html}</body>
        <script>${js}</script>
    </html>`;
  };

  return (
    <div className="explorePage">
      <div className="explorePage-main-container">
        <div className="explorePage-sub-container">
          <iframe
            className="iframe"
            srcDoc={exportModule(ex1)}
            title="output"
            sandbox="allow-scripts"
            frameBorder="0"
            width="100%"
            height="100%"
          />
        </div>
      </div>
    </div>
  );
}

export default ExplorePage;
